        height: '100',
        width: '100',
    })
    const {icon, width, height, fill} = toRefs(props)

    const store = useStore()

    const themeFill = ref(fill?.value || getComputedStyle(document.documentElement).getPropertyValue('--theme-text-color'))

    watch(() => store.state.settingsDarkMode, (newVal, oldVal) => {
        themeFill.value = fill?.value || getComputedStyle(document.documentElement).getPropertyValue(`--theme-text-color`)
    })

</script>

